let cardContainer=document.getElementById("app"),createCard=document.getElementsByClassName("create-card"),createPlus=document.getElementsByClassName("create"),counters={count0:1,count1:1,count2:1};function getElements(){createCard=document.getElementsByClassName("create-card"),createPlus=document.getElementsByClassName("create")}let app=new Vue({el:"#app",data:{cards0:[{num:0,id:0,title:"Ачма",text:"Рецепт грузинского пирога с сыром, готовящегося из большого количества слоёв теста, которое немного отваривают. В качестве начинки для ачмы можно использовать сулугуни, осетинский сыр или их смесь."}],cards1:[{num:1,id:0,title:"Бефстроганов классический",text:"Что может быть приятнее домашнего обеда или ужина! Бефстроганов и картофельное пюре – идеальное блюдо как раз на такой случай. Мягкие кусочки говядины в томатно-сливочном соусе, да с нежной, воздушной картошечкой. Это очень вкусно! Вся семья останется сыта и довольна."},{num:1,id:1,title:"Пышные пончики в духовке",text:"Быстро и без жарки! Нежнейшие пончики (англ. donuts) в духовке - без фритюра! Очень-очень воздушные, мягкие и пышные пончики, без начинки. Просто объедение!"}],cards2:[{num:2,id:0,title:"Бешбармак",text:"Бешбармак в переводе с тюркских языков - «пять пальцев», так сложилось, что это блюдо едят руками. На тонкое нежное тесто выкладывают кусочки томленой в бульоне баранины, приправленной специями и луком. Вкус бешбармака превосходит все ожидания. С нашим рецептом вы сможете приготовить бешбармак дома, и, будьте уверены, время, потраченное на его приготовление, того стоит."}],darkMode:!1},methods:{addData:function(e,t,a){let d=counters["count"+e];this["cards"+e].push({id:d,num:e,title:t,text:a}),counters["count"+e]++},removeData:function(e,t){for(let a=0;a<this["cards"+e].length;a++)this["cards"+e][a].id===t&&(this["cards"+e].splice(a,1),counters["count"+e]--)}}});for(let e=0;e<createPlus.length;e++)createPlus[e].addEventListener("click",t=>{createPlus[e].style.display="none",createCard[e].insertAdjacentHTML("afterbegin",`<form id="form${e}" class="create-form">\n    <label for="title${e}">Название</label>\n    <input v-model="card-title${e}" placeholder="Название" type="text" id="title${e}">\n    <label for="text${e}">Текст</label>\n    <textarea v-model="card-text${e}" placeholder="Текст" type="text" id="text${e}"></textarea>\n    <button id="create${e}">Создать</button>\n  </form>`);let a=document.getElementById("form"+e),d=document.getElementById("create"+e),s=document.getElementById("title"+e),l=document.getElementById("text"+e);a.addEventListener("submit",e=>e.preventDefault()),d.addEventListener("click",t=>{app.addData(e,s.value,l.value),a.remove(),createPlus[e].style.display="block"})});let dark=document.getElementById("dark-mode"),light=document.getElementById("light-mode"),body=document.getElementsByTagName("body")[0],footer=document.getElementsByTagName("footer")[0],apps=document.getElementsByClassName("app"),cards=document.getElementsByClassName("card"),createSigns=document.getElementsByClassName("create");dark.addEventListener("click",e=>{body.classList.add("dark"),footer.classList.add("dark");for(let e=0;e<apps.length;e++)apps[e].classList.add("dark");for(let e=0;e<cards.length;e++)cards[e].classList.add("dark");for(let e=0;e<createSigns.length;e++)createSigns[e].classList.add("dark");dark.style.display="none",light.style.display="inline",app.darkMode=!0}),light.addEventListener("click",e=>{body.classList.remove("dark"),footer.classList.remove("dark");for(let e=0;e<apps.length;e++)apps[e].classList.remove("dark");for(let e=0;e<cards.length;e++)cards[e].classList.remove("dark");for(let e=0;e<createSigns.length;e++)createSigns[e].classList.remove("dark");light.style.display="none",dark.style.display="inline",app.darkMode=!1});